import React,{useEffect,useState} from 'react';
import fireDb from '../firebase';
import { useHistory,useParams,Link } from 'react-router-dom';
import './view.css';

const View = () => {
  const [an,setAn] = useState({});
  const {id} = useParams();

  useEffect(() => {
    fireDb.child(`list/${id}`).get().then((snapshot) => {
      
      if(snapshot.exists()){
        setAn({...snapshot.val()});
      }else{
        setAn({});
      }
    });
  },[id]);

  console.log("an",an);
  return (
    <div>
        <span>{an.name}</span>
    </div>



  );
};

export default View;
